mapper.select:
select a.id, a.user_id, a.submit_date, a.state, a.type, a.audit_desc, a.loan_value, a.parent_type, jcb.company as companyName, jcb.id as companyId, (select c.value from sys_dict c where c.type=1002 and c.code = a.type) as typeName, (select c.value from sys_dict c where c.type=1004 and c.code = jcb.tec_domain) as tecName from jltfisp_business_applay a ,jltfisp_co_base jcb
a.user_id =jcb.user_id and jcb.business_type=a.type
order by #{_BY} limit #{_LM} offset #{_ST}

mapper.update:
        update jltfisp_business_applay a set a.status = 'd',a.update_at =now(),a.update_by=#userId
        where 
	    a.id=#{id,jdbcType=INTEGER}

update jltfisp_business_applay
user_id=#{userId,jdbcType=INTEGER}, submit_date=#{submitDate,jdbcType=DATE}, state=#{state,jdbcType=INTEGER}, type=#{type,jdbcType=CHAR}, audit_desc=#{auditDesc,jdbcType=VARCHAR}, loan_value=#{loanValue,jdbcType=VARCHAR}, parent_type=#{parentType,jdbcType=VARCHAR},
where id=#{id,jdbcType=INTEGER}

mapper.select:
		select

        from jltfisp_business_applay a
        where 
	    	a.user_id = #{userId,jdbcType=INTEGER} 
	    	and a.type = #{type,jdbcType=CHAR} 
	    	and a.state in (0,1)

		select

        from jltfisp_business_applay a
        where 
	    	a.user_id = #{userId,jdbcType=INTEGER} 
	    	and a.type = #{type,jdbcType=CHAR} 
	    	and a.state = #{state,jdbcType=INTEGER}
